// import { StatusBar } from "expo-status-bar";
import React, { useState } from "react";
import {
  StyleSheet,
  Text,
  View,
  ImageBackground,
  TextInput,
  secureTextEntry,
  TouchableOpacity,
  Keyboard,
  TouchableWithoutFeedback,
} from "react-native";
// import * as Font from "expo-font";
// import AppLoading from "expo-app-loading";


export default function RegistrationScreen({navigation}) {
  const [isKeyboard, setIsKeyboard] = useState(false);
  // const [state, setState] = useState(initial)
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [login, setLogin] = useState("");


  const stateString = () => {
    setLogin('');
    setEmail("");
    setPassword("");
  };
  const pressButton = () => {
    setIsKeyboard(false);
    Keyboard.dismiss();
    console.log(login, email, password);
    stateString();
  };



  return (
    <TouchableWithoutFeedback
      onPress={() => {
        setIsKeyboard(false);
        Keyboard.dismiss();
      }}
    >
      <View style={styles.container}>
        <ImageBackground
          style={styles.img}
          source={require("../../../assets/img/photo.jpg")}
        >
          <View style={{ ...styles.form, marginBottom: isKeyboard ? 5 : 50 }}>
            <View
              style={{
                ...styles.header,
                // height: isKeyboard ? 0 : "30%"
                display: isKeyboard ? 'none' : 'flex'
              }}
            >
              <Text style={styles.headerText}>Registration Screen</Text>

            </View>
            <TouchableOpacity
              style={{width: 30}}
              activeOpacity={0.5}
            >
                    <Text style={{...styles.text, textAlign:'left'}} onPress={() => navigation.navigate('Home')}>Home</Text>
                </TouchableOpacity>
            <View>
              <Text style={styles.text}> Login</Text>
              <TextInput
                value={login}
                onChangeText={(val) => {
                  setLogin(val);
                }}
                style={styles.input}
                onFocus={() => {
                  setIsKeyboard(true);
                }}
              />
            </View>
            <View style={{ marginTop: 10 }}>
              <Text style={styles.text}> Email</Text>
              <TextInput
                value={email}
                onChangeText={(val) => {
                  setEmail(val);
                }}                
                style={styles.input}
                onFocus={() => {
                  setIsKeyboard(true);
                }}
              />
            </View>
            <View style={{ marginTop: 10 }}>
              <Text style={styles.text}> Password</Text>
              <TextInput
                value={password}
                onChangeText={(val) => {
                  setPassword(val);
                }}
                secureTextEntry={true}
                style={styles.input}
                onFocus={() => {
                  setIsKeyboard(true);
                }}
              />
            </View>
            <TouchableOpacity
              onPress={pressButton}
              activeOpacity={0.5}
              style={styles.btn}
            >
              <Text style={styles.btnText}>Log in</Text>
            </TouchableOpacity>
            <TouchableOpacity
              activeOpacity={0.5}
            >
                    <Text style={{...styles.text, textAlign:'right', paddingTop:20}} onPress={() => navigation.navigate('Login')}>Go to Login</Text>
                </TouchableOpacity>
          </View>
        </ImageBackground>
        {/* <StatusBar style='auto' /> */}
      </View>
    </TouchableWithoutFeedback>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
  },
  img: {
    flex: 1,
    resizeMode: "cover",
    justifyContent: "flex-end",
  },
  form: {
    marginHorizontal: 20,
  },
  header: {
   paddingTop: 30,
    justifyContent: "center",
  },
  headerText: {
    color: "#fff",
    fontSize: 50,
    textAlign: "center",
    fontFamily: "DancingScript-Regular",
  },
  text: {
    fontSize: 25,
    fontWeight: "900",
    color: "#fff",
    textAlign: "center",
    marginBottom: 5,
    fontFamily: "DancingScript-Regular",
  },
  input: {
    borderWidth: 2,
    borderColor: "#fff",
    borderRadius: 10,
    height: 40,
    color: "#fff",
    textAlign: "center",
    fontFamily: "DancingScript-Regular",
    fontWeight: "900",
    fontSize: 20,
  },
  btn: {
    marginTop: 40,
    height: 40,
    backgroundColor: `#0000cd`,
    justifyContent: "center",
    borderRadius: 10,
    width: "50%",
    marginLeft: "auto",
    marginRight: "auto",
  },
  btnText: {
    color: "#fff",
    textAlign: "center",
    fontSize: 18,
    fontFamily: "DancingScript-Regular",
  },
});
